    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        for (int i = 0; i < KayitEkrani.hastalar.size(); i++) {
            if(KayitEkrani.hastalar.get(i).getKullaniciAdi().equals(jTextField1.getText()) && KayitEkrani.hastalar.get(i).getSifre().equals(jPasswordField1.getText())) {
                HastahaneFormu hastahaneFormu = new HastahaneFormu();
                hastahaneFormu.setVisible(true);
                this.setVisible(false);
                break;
            }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

